import { useEffect, useState } from "react"
import "./portfolio.scss"
import PortfolioList from "./portfolioList/PortfolioList"
import {featurePortfolio, webPortfolio, mobilePortfolio, designPortfolio, contentPortfolio} from '../../data'

const Portfolio = () => {

  const [selected, setSelected] = useState("featured")
  const [data, setData] = useState([])


  const list = [
    {
      id: 'featured',
      title: 'Featured'
    },
    {
      id: 'web',
      title: 'Web App'
    },
    {
      id: 'mobile',
      title: 'Mobile App'
    },
    {
      id: 'design',
      title: 'Design'
    },
    {
      id: 'content',
      title: 'Content'
    },
  ]

  useEffect(()=>{
    switch (selected) {
      case 'featured': setData(featurePortfolio) 
        break;
      case 'web': setData(webPortfolio) 
        break;
      case 'mobile': setData(mobilePortfolio) 
        break;
      case 'design': setData(designPortfolio) 
        break;
      case 'content': setData(contentPortfolio) 
        break;
    
      default:
        break;
    }
  },[selected])

  return (
    <div className="portfolio" id="portfolio">
      <h1>Portfolio</h1>
      <ul>
        {list.map((item)=>(
          <PortfolioList title={item.title} active={selected === item.id} setSelected={setSelected} id={item.id}/>
        ))}
      </ul>
      <div className="container">
        <div className="item">
          <img src="https://source.unsplash.com/random/200x200/?mobile banking" alt="" />
          <h3>Banking App</h3>
        </div>
        <div className="item">
          <img src="assets/bankingapp.jpg" alt="" />
          <h3>Banking App</h3>
        </div>
        <div className="item">
          <img src="assets/bankingapp.jpg" alt="" />
          <h3>Banking App</h3>
        </div>
        <div className="item">
          <img src="assets/bankingapp.jpg" alt="" />
          <h3>Banking App</h3>
        </div>
        <div className="item">
          <img src="assets/bankingapp.jpg" alt="" />
          <h3>Banking App</h3>
        </div>
        <div className="item">
          <img src="assets/bankingapp.jpg" alt="" />
          <h3>Banking App</h3>
        </div>
      </div>
    </div>
  )
}

export default Portfolio
